import React, {useState, useEffect} from 'react';
import { Link, useParams } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { useDispatch, useSelector } from 'react-redux'
import { listProductDetails } from '../actions/productActions'


function ProductScreen({ history }) {
    const {id} = useParams()
    const [qty, setQty] = useState(1)
    const dispatch = useDispatch()
    const productDetails = useSelector(state=>state.productDetails)
    const {loading, error, product} = productDetails
    useEffect(() => {
        dispatch(listProductDetails(id))
    }, [dispatch])


    const addToCartHandler = () => {
        if (history && typeof history.push === 'function') {
          history.push(`/cart/${id}?qty=${qty}`);
        } else {
          console.error('Failed to navigate: history.push is not a function or undefined.');
        }
    }

    return <div>
            <Link to='/' className='btn btn-light my-3'>Go Back</Link>
            {loading ?
                <Loader/>
                : error
                    ? <Message variant='danger'>{error}</Message>
                :(
                    <Row>
                        <Col md={3}>
                            <Image src={product.image} alt={product.name} fluid/>
                        </Col>
                        <Col md={6}>
                            <ListGroup variant='flush'>
                                <ListGroup.Item>
                                    <h1>{product.name}</h1>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <h5><Rating value={product.rating} text={' ' + product.numReviews + ' reviews'} color={'#f8e825'}/></h5>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <h4>Price: ${product.price}</h4>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <h5>{product.description}</h5>
                                </ListGroup.Item>
                            </ListGroup>
                        </Col>
                        <Col md={3}>
                            <Card>
                                <ListGroup variant='flush'>
                                    <ListGroup.Item>
                                        <Row>
                                            <Col>Price:</Col>
                                            <Col>
                                                <strong>${product.price}</strong>
                                            </Col>
                                        </Row>
                                    </ListGroup.Item>
                                    <ListGroup.Item>
                                        <Row>
                                            <Col>Status:</Col>
                                            <Col>
                                                <strong>{product.countInStock > 0 ? 'In Stock' : 'Out of Stock'}</strong>
                                            </Col>
                                        </Row>
                                    </ListGroup.Item>
                                    {product.countInStock > 0 && (
                                        <ListGroup.Item>
                                            <Row>
                                                <Col>Qty</Col>
                                                <Col xs='auto' className='my-1'>
                                                    <Form.Control
                                                        as='select'
                                                        value={qty}
                                                        onChange={(e) => setQty(e.target.value)}
                                                    >
                                                        {
                                                            [...Array(product.countInStock).keys()].map((x)=>(
                                                                <option key={x+1} value={x+1}>
                                                                    {x + 1}
                                                                </option>
                                                            ))
                                                        }
                                                    </Form.Control>
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>

                                    )}
                                    <ListGroup.Item>
                                        <Button onClick={addToCartHandler} className="btn btn-lg btn-primary" disabled={product.countInStock == 0} type='button'>Add to Cart</Button>
                                    </ListGroup.Item>
                                </ListGroup>
                            </Card>
                        </Col>
                    </Row>
                )
            } 
        </div>;
    }

    export default ProductScreen;
