import React, { useState } from 'react';
import { Button, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import Autosuggest from 'react-autosuggest';

// Mock data for suggestions
const suggestions = [
  'Apple',
  'Banana',
  'Cherry',
  'Grapes',
  'Lemon',
  'Orange',
];

function SearchBox() {
  const [keyword, setKeyword] = useState('');
  const [suggestionValue, setSuggestionValue] = useState('');
  const [suggestionsList, setSuggestionsList] = useState([]);

  let navigate = useNavigate();

  const getSuggestions = (value) => {
    const inputValue = value.trim().toLowerCase();
    const inputLength = inputValue.length;
    const filteredSuggestions = inputLength === 0
      ? []
      : suggestions.filter(suggestion =>
          suggestion.toLowerCase().slice(0, inputLength) === inputValue
        );

    setSuggestionsList(filteredSuggestions);
  };

  const onSuggestionSelected = (event, { suggestionValue }) => {
    setKeyword(suggestionValue);
    navigate(`/?keyword=${suggestionValue}&page=1`);
  };

  const inputProps = {
    placeholder: 'Search...',
    value: keyword,
    onChange: (event, { newValue }) => {
      setKeyword(newValue);
    },
  };

  return (
    <Form inline style={{ display: 'flex', justifyContent: 'space-between' }}>
      <Autosuggest
        suggestions={suggestionsList}
        onSuggestionsFetchRequested={({ value }) => getSuggestions(value)}
        onSuggestionsClearRequested={() => setSuggestionsList([])}
        getSuggestionValue={(suggestion) => suggestion}
        renderSuggestion={(suggestion) => <div>{suggestion}</div>}
        inputProps={inputProps}
        onSuggestionSelected={onSuggestionSelected}
      />

      <Button
        type='submit'
        variant='primary'
        className='p-2'
        style={{
          border: 'none',
          borderRadius: '4px',
          padding: '0.5rem',
          marginLeft: '0.5rem',
          width: '3rem',
          height: '3rem',
        }}
      >
        <i className='fas fa-search'></i>
      </Button>
    </Form>
  );
}

export default SearchBox;
