import React, {useState, useEffect} from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { useDispatch, useSelector } from 'react-redux'
import { listProductDetails, createProductReview } from '../actions/productActions'
import { PRODUCT_CREATE_REVIEW_RESET } from '../constants/productConstants'


function ProductScreen() {
    const {id} = useParams()
    const navigate = useNavigate()
    const [qty, setQty] = useState(1)
    const dispatch = useDispatch()
    
    const productDetails = useSelector(state=>state.productDetails)
    const {loading, error, product} = productDetails
    
    const [rating, setRating] = useState(0)
    const [comment, setComment] = useState('')
   
    const userLogin = useSelector(state=>state.userLogin)
    const {userInfo} = userLogin 

    const productReviewCreate = useSelector(state=>state.productReviewCreate)
    const {loading: loadingProductReview, error: errorProductReview, success: successProductReview} = productReviewCreate 


    useEffect(() => {
        dispatch(listProductDetails(id))
    }, [dispatch])

    const addToCartHandler = () => {
        navigate(`/cart/${id}?qty=${qty}`)
    }

    function handleClick() {
        navigate(-1)
    }

    return <div>
            <Link onClick={handleClick} className='btn btn-light my-3'>Go Back</Link>
            {loading ?
                <Loader/>
                : error
                    ? <Message variant='danger'>{error}</Message>
                :(
                    <div>
                        <Row>
                            <Col md={3}>
                                <Image
                                src={product.image}
                                alt={product.name}
                                fluid
                                style={{ transition: 'transform 0.5s', borderRadius: '8px' }}
                                onMouseOver={e => e.target.style.transform = 'scale(1.05)'}
                                onMouseOut={e => e.target.style.transform = 'scale(1)'}
                                />                        
                            </Col>
                            <Col md={6}>
                                <ListGroup variant='flush'>
                                    <ListGroup.Item>
                                        <h1>{product.name}</h1>
                                    </ListGroup.Item>
                                    <ListGroup.Item>
                                        <h5
                                            style={{ marginTop: '0.5rem' }}
                                            title={`${product.rating} stars, ${product.numReviews} reviews`}
                                        >
                                            <Rating value={product.rating} text={` ${product.numReviews} reviews`} color={'#f8e825'} />
                                        </h5>                                
                                    </ListGroup.Item>
                                    <ListGroup.Item>
                                        <h4>Price: ${product.price}</h4>
                                    </ListGroup.Item>
                                    <ListGroup.Item>
                                        <h5>{product.description}</h5>
                                    </ListGroup.Item>
                                </ListGroup>
                            </Col>
                            <Col md={3}>
                                <Card>
                                    <ListGroup variant='flush'>
                                        <ListGroup.Item>
                                            <Row>
                                                <Col>Price:</Col>
                                                <Col>
                                                    <strong>${product.price}</strong>
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                        <ListGroup.Item>
                                            <Row>
                                                <Col>Status:</Col>
                                                <Col>
                                                    <strong>{product.countInStock > 0 ? 'In Stock' : 'Out of Stock'}</strong>
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                        {product.countInStock > 0 && (
                                            <ListGroup.Item>
                                                <Row>
                                                    <Col>Quantity:</Col>
                                                    <Col xs='auto' className='my-1'>
                                                        <Form.Control
                                                            as='select'
                                                            value={qty}
                                                            onChange={(e) => setQty(e.target.value)}
                                                        >
                                                            {
                                                                [...Array(product.countInStock).keys()].map((x)=>(
                                                                    <option key={x+1} value={x+1}>
                                                                        {x + 1}
                                                                    </option>
                                                                ))
                                                            }
                                                        </Form.Control>
                                                    </Col>
                                                </Row>
                                            </ListGroup.Item>

                                        )}
                                        <ListGroup.Item>
                                            <Button onClick={addToCartHandler} className="btn btn-lg btn-primary" disabled={product.countInStock == 0} type='button'>Add to Cart</Button>
                                        </ListGroup.Item>
                                    </ListGroup>
                                </Card>
                            </Col>
                        </Row>

                        <Row>
                            <Col md={6}>
                                <h4>Reviews</h4>
                                {product.reviews.length === 0 && <Message variant='info'>No Reviews</Message>}
                            
                                <ListGroup variant='flush'>
                                    {product.reviews.map((review) => (
                                        <ListGroup.Item key={review._id}>
                                            <strong>{review.name}</strong>
                                            <Rating value={review.rating} color='#f8e825'/>
                                            <p>{new Date(review.createdAt).toLocaleDateString()}</p>
                                            <p>{review.comment}</p>
                                        </ListGroup.Item>
                                    ))}
                                </ListGroup>
                            </Col>
                        </Row>
                    </div>
                )
            } 
        </div>;
    }

    export default ProductScreen;
