import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { LinkContainer } from 'react-router-bootstrap'
import Loader from '../components/Loader';
import { getCategories } from '../actions/productActions'

const ScrollToTop = () => {
    const [showCategory, setShowCategory] = useState(false);
    const [buttonClicked, setButtonClicked] = useState(false)

    const dispatch = useDispatch();

    const productCategories = useSelector(state => state.productCategories);
    const categories = productCategories.categories.categories;
    const { loading: loadingCategories } = productCategories;

    useEffect(() => {
        const handleScroll = () => {
            setShowCategory(window.scrollY > window.innerHeight / 8);
        };

        window.addEventListener('scroll', handleScroll)

        return () => {
            window.removeEventListener('scroll', handleScroll);
        }
    }, [])

    useEffect(() => {
        dispatch(getCategories())
    })

    const handleCategoryClick = () => {
        setButtonClicked(true)
    };

    const handleCategoryReClick = () => {
        setButtonClicked(false)
    };

    return (
        <>
            {loadingCategories ? (
                <Loader />
            ) : (
                <>
                    {buttonClicked ? (
                        <div
                        style={{
                            position: 'fixed',
                            top: '40px',
                            right: '20px',
                            width: '250px',
                            height: '75px',
                            backgroundColor: '#000',
                            borderRadius: '10px',
                            display: 'flex',
                            justifyContent: 'center',
                            alignItems: 'center',
                            cursor: 'pointer',
                            transition: 'all 0.3s ease-in-out',
                            opacity: '1',
                            pointerEvents: 'all',
                            zIndex: '999'
                        }}
                        onClick={handleCategoryReClick}
                        >
                            <h4>Categories</h4>
                            {categories.map((category) => (
                                <LinkContainer to={{
                                    pathname: '/',
                                    search: `?keyword=${encodeURIComponent(category)}`
                                }}>
                                    <li>{category}</li>
                                </LinkContainer>
                            ))}
                        </div>

                    ) : (
                        <div
                        className={`categories ${showCategory ? 'show' : ''}`}
                        onClick={handleCategoryClick}
                        role='button'
                        tabIndex={0}
                        style={{ position: 'fixed', top: '40px', right: '20px'}}
                        >
 
                            <i className='fa-sharp fa-solid fa-cubes-stacked fa-2xl' style={{color: '#e6e6e6'}}></i>
                            
                        </div>
                    )}

                </>
            )}
        </>
    )
}

export default ScrollToTop;
