import React, { useState, useRef, useEffect } from 'react';
import { OverlayTrigger, Popover, Button } from 'react-bootstrap';

function ArrowElement() {
    const [showPopover, setShowPopover] = useState(false);
    const [arrowPosition, setArrowPosition] = useState({ top: 0, left: 0 });
    const elementRef = useRef(null);

    useEffect(() => {
        const handleScroll = () => {
            const { top, left } = elementRef.current.getBoundingClientRect();
            setArrowPosition({ top, left });
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleArrowClick = () => {
        setShowPopover(!showPopover);
    };

    const popover = (
        <Popover id="popover-basic">
            <Popover.Content>
                <Button variant="light" onClick={handleArrowClick}>
                    Categories
                </Button>
                {/* Add your expanded content here */}
            </Popover.Content>
        </Popover>
    );

    return (
        <div style={{ position: 'fixed', right: '10px', top: arrowPosition.top }}>
            <OverlayTrigger
                trigger="click"
                placement="left"
                show={showPopover}
                overlay={popover}
                rootClose
            >
                <Button variant="light" ref={elementRef} onClick={handleArrowClick}>
                    <span>&#8594;</span>
                </Button>
            </OverlayTrigger>
        </div>
    );
}

export default ArrowElement;
