import React, { useState } from 'react';
import { Accordion, Button } from 'react-bootstrap';

const ArrowElement = () => {
    const [expanded, setExpanded] = useState(false);

    const handleToggle = () => {
        setExpanded(!expanded);
    };

    return (
        <div
            style={{
                position: 'fixed',
                right: '10px',
                top: '50%',
                transform: 'translateY(-50%)',
                zIndex: '999',
                backgroundColor: '#f8f9fa',
                borderRadius: '50%',
                padding: '10px',
                cursor: 'pointer'
            }}
        >
            <Accordion defaultActiveKey='0' activeKey={expanded ? '0' : ''}>
                <Accordion.Toggle as={Button} variant='link' eventKey='0' onClick={handleToggle}>
                    <i className={`bi bi-chevron-${expanded ? 'left' : 'right'}`}></i>
                </Accordion.Toggle>
                <Accordion.Collapse eventKey='0'>
                    <div style={{ padding: '10px' }}>Categories</div>
                </Accordion.Collapse>
            </Accordion>
        </div>
    );
};

export default ArrowElement;
