import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Loader from '../components/Loader';

const ScrollToTop = () => {
    const [showCategory, setShowCategory] = useState(false);
    const [buttonClicked, setButtonClicked] = useState(false); // new state for tracking button click
    const dispatch = useDispatch();
    const productCategories = useSelector(state => state.productCategories);
    const categories = productCategories.categories.categories;
    const { loading: loadingCategories } = productCategories;

    useEffect(() => {
        const handleScroll = () => {
            setShowCategory(window.scrollY > window.innerHeight / 8);
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleCategoryClick = () => {
        setButtonClicked(true); // set buttonClicked state to true on click
        setShowCategory(true);
    };

    return (
        <>
            {loadingCategories ? (
                <Loader />
            ) : (
                <>
                    {buttonClicked ? ( // check if button has been clicked
                        <div
                            style={{
                                width: '200px', // example width
                                height: '100px', // example height
                                backgroundColor: 'red', // example background color
                            }}
                        >
                            {/* Content for the bigger rectangle */}
                        </div>
                    ) : (
                        <button onClick={handleCategoryClick}>Click me</button>
                    )}
                </>
            )}
        </>
    );
};

export default ScrollToTop;
