import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Loader from '../components/Loader';

const ScrollToTop = () => {
    const [showDropdown, setShowDropdown] = useState(false);

    const dispatch = useDispatch();

    const productCategories = useSelector(state => state.productCategories);
    const categories = productCategories.categories.categories;
    const { loading: loadingCategories } = productCategories;

    useEffect(() => {
        const handleScroll = () => {
            setShowDropdown(window.scrollY > window.innerHeight / 8);
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleCategoryClick = () => {
        setShowDropdown(true);
    };

    return (
        <>
            {loadingCategories ? (
                <Loader />
            ) : (
                <>
                <div
                    className={`categories`}
                    role='button'
                    tabIndex={0}
                    style={{ position: 'fixed', top: '40px', right: '20px'}}
                >
                    <i className='fa-sharp fa-solid fa-cubes-stacked fa-2xl' style={{color: '#e6e6e6'}} onClick={() => setShowDropdown(!showDropdown)}></i>
                </div>
                {showDropdown && (
                    <div className="dropdown" style={{position: 'absolute', top: '60px', right: '20px'}}>
                        <ul>
                            {categories.map(category => (
                                <li key={category}>{category}</li>
                            ))}
                        </ul>
                    </div>
                )}
                </>
            )}
        </>
    )
}

export default ScrollToTop;
