import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Loader from '../components/Loader';

const ScrollToTop = () => {
    const [showCategory, setShowCategory] = useState(false);

    const dispatch = useDispatch();

    const productCategories = useSelector(state => state.productCategories);
    const categories = productCategories.categories.categories;
    const { loading: loadingCategories } = productCategories;

    useEffect(() => {
        const handleScroll = () => {
            setShowCategory(window.scrollY > window.innerHeight / 8);
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleCategoryClick = () => {
        setShowCategory(!showCategory);
    };

    return (
        <>
            {loadingCategories ? (
                <Loader />
            ) : (
                <>
                    <div
                        className={`categories ${showCategory ? 'show' : ''}`}
                        onClick={handleCategoryClick}
                        role='button'
                        tabIndex={0}
                    >
                        <i className='fa-sharp fa-solid fa-cubes-stacked fa-2xl'></i>
                    </div>
                    {showCategory && (
                        <div className="category-dropdown">
                            <ul>
                                {categories.map(category => (
                                    <li key={category.id}>{category.name}</li>
                                ))}
                            </ul>
                        </div>
                    )}
                </>
            )}
        </>
    );
};

export default ScrollToTop;
