import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Loader from '../components/Loader';

const ScrollToTop = () => {
    const [showCategory, setShowCategory] = useState(false);

    const dispatch = useDispatch();

    const productCategories = useSelector(state => state.productCategories);
    const categories = productCategories.categories.categories;
    const { loading: loadingCategories } = productCategories;

    useEffect(() => {
        const handleScroll = () => {
            setShowCategory(window.scrollY > window.innerHeight / 8);
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleCategoryClick = () => {
        setShowCategory(true);
    };

    return (
        <>
            {loadingCategories ? (
                <Loader />
            ) : (
                <>
                <div
                    className={`categories ${showCategory ? 'show' : ''}`}
                    onClick={handleCategoryClick}
                    role='button'
                    tabIndex={0}
                    style={{ position: 'fixed', top: '40px', right: '20px'}}
                >
                    <i className='fa-sharp fa-solid fa-cubes-stacked fa-2xl' style={{color: '#e6e6e6'}}></i>
                </div>
                {!showCategory && (
                    <div className="dropdown">
                            <ul>
                                {categories.map(category => (
                                    <li key={category.id}>{category.name}</li>
                                ))}
                            </ul>
                    </div>
                )}
                </>
            )}
        </>
    )
}

export default ScrollToTop;
