import React, { useState, useEffect } from 'react'

const ScrollToTop = () => {
    const [showCategory, setShowCategory] = useState(false)

    useEffect(() => {
        const handleScroll = () => {
            setShowCategory(window.scrollY > window.innerHeight / 8)
        }

        window.addEventListener('scroll', handleScroll)

        return () => {
            window.removeEventListener('scroll', handleScroll)
        }
    }, [])

    const handleCategoryClick = () => {
        console.log('cock')
    }

    return (
        <div
            className={`categories ${showCategory ? 'show' : ''}`}
            onClick={handleCategoryClick}
            role='button'
            tabIndex={0}
            style={{ position: 'fixed', top: '40px', right: '20px'}}
        >
            <i class='fa-solid fa-boxes-stacked fa-2xl' style={{color: '#e6e6e6'}}></i>
        </div>
    )
}

export default ScrollToTop
