import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import { Form, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Loader from "../components/Loader"
import Message from "../components/Message"
import FormContainer from "../components/FormContainer"
import { getPromoCodeDetails } from '../actions/orderActions'
import { useNavigate, useParams } from "react-router-dom";

function PromoCodeEditScreen() {

    const { id } = useParams()

    const [promoCode, setPromoCode] = useState('')
    const [discount, setDiscount] = useState('')

    const navigate = useNavigate()
    const dispatch = useDispatch()

    const promoCodeDetails = useSelector(state => state.promoCodeDetails)
    const { error, loading, promoCode: promoCodeList } = promoCodeDetails


    
    useEffect(() => {
        if (!promoCodeList.promoCode || promoCodeList.id !== Number(id)) {
            dispatch(getPromoCodeDetails(id))
        } else {
            setPromoCode(promoCodeList.promoCode)
            setDiscount(promoCodeList.discount)
        }
    }, [promoCodeList, id])

    const submitHandler = (e) => {
        e.preventDefault()

    }


    return (
        <div>
            <Link to='/admin/promocodelist' className="btn btn-light my-3">Go Back</Link>

            <FormContainer>
                <h1 style={{ fontSize: '3rem', marginBottom: '1rem' }}>Edit Promo Code</h1>
                {loading ? <Loader/> : error ? <Message variant='danger'>{error}</Message> : (
                    <Form style={{ maxWidth: '600px', margin: '0 auto' }} onSubmit={submitHandler}>

                        <Form.Group controlId='promoCode' style={{ marginBottom: '1.5rem' }}>
                            <Form.Label>Promo Code</Form.Label>
                            <Form.Control type='text' placeholder='Enter Promo Code Name' value={promoCode} onChange={(e) => setPromoCode(e.target.value)} style={{ fontSize: '1.5rem', padding: '1rem' }} />
                        </Form.Group>

                        <Form.Group controlId='discount' style={{ marginBottom: '1.5rem' }}>
                            <Form.Label>Discount</Form.Label>
                            <Form.Control type='discount' placeholder='Enter Discount' value={discount} onChange={(e) => setDiscount(e.target.value)} style={{ fontSize: '1.5rem', padding: '1rem' }} />
                        </Form.Group>

                        <Button type='submit' variant='primary' style={{ fontSize: '1.5rem', padding: '1rem 2rem', borderRadius: '4px', display: 'block', width: '100%', marginTop: '2rem', marginBottom: '2rem' }}>Update</Button>
                    </Form>
                )}
            </FormContainer>
            
        </div>
    )
}

export default PromoCodeEditScreen
