import {
  Row,
  Col,
  ListGroup,
  Image,
  Form,
  Button,
  Card,
} from 'react-bootstrap';
import Loader from '../components/Loader';
import Message from '../components/Message';
import React, {useState, useEffect} from "react";

function AboutUsScreen() {
    const [images, setImages] = useState([]);

    useEffect(() => {
      fetch('/static/images/about0.jpg')
        .then(response => response.blob())
        .then(blob => {
          let objectURL = URL.createObjectURL(blob);
          setImages(images => [...images, {id: 'about0', image: objectURL}]);
        })
        .catch(error => console.log(error));
  
      fetch('/static/images/about1.jpg')
        .then(response => response.blob())
        .then(blob => {
          let objectURL = URL.createObjectURL(blob);
          setImages(images => [...images, {id: 'about1', image: objectURL}]);
        })
        .catch(error => console.log(error));
  
      fetch('/static/images/about2.jpg')
        .then(response => response.blob())
        .then(blob => {
          let objectURL = URL.createObjectURL(blob);
          setImages(images => [...images, {id: 'about2', image: objectURL}]);
        })
        .catch(error => console.log(error));
    }, []);
    const loading = false; 
    const error = null;

  return (
    <div className='body'>
      <h1>About NutriNation</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Row className='mt-3'>
          <Col md={6}>
            {images.map((img, i) => (
              <Image key={i} src={img} className='mb-3' fluid rounded />
            ))}
          </Col>
          <Col md={6}>
            <Card className='shadow mb-3'> 
                <Card.Header><h3>About Us</h3></Card.Header> 
                    <Card.Body> 
                        <Card.Text>
                            At Nutrination, we believe that living a <span style={{ color: 'rgb(235, 64, 52)' }}>healthy and fit lifestyle</span> can be made easier, simpler, and more accessible. That's why we have made it our mission to provide our customers with <span style={{ color: 'rgb(235, 64, 52)' }}>top-quality</span>, <span style={{ color: 'rgb(235, 64, 52)' }}>science-backed</span> food supplements, including <span style={{ color: 'rgb(235, 64, 52)' }}>protein, keratine, vitamins</span>, and <span style={{ color: 'rgb(235, 64, 52)' }}>more, all at affordable prices</span>.
                        </Card.Text> 
                        <Card.Text>
                            Our team of experts carefully curates our product selection, working with trusted suppliers and manufacturers, to ensure that our customers receive only the <span style={{ color: 'rgb(235, 64, 52)' }}>best supplements</span> that are <span style={{ color: 'rgb(235, 64, 52)' }}>backed by research and proven to be effective</span>. We also strive to provide our customers with <span style={{ color: 'rgb(235, 64, 52)' }}>unparalleled customer service</span>, making sure that our <span style={{ color: 'rgb(235, 64, 52)' }}>friendly and knowledgeable staff</span> are always available to answer any questions or concerns.
                        </Card.Text> 
                    </Card.Body> 
            </Card>
            <Card className='shadow mb-3'> 
                <Card.Header><h3>Our Mission</h3></Card.Header> 
                    <Card.Body> 
                        <Card.Text>
                            Our <span style={{ color: 'rgb(235, 64, 52)' }}>mission</span> is to provide our customers with the <span style={{ color: 'rgb(235, 64, 52)' }}>highest-quality, science-backed food supplements</span> that will help them <span style={{ color: 'rgb(235, 64, 52)' }}>achieve their fitness goals and maintain optimal health</span>. We believe that <span style={{ color: 'rgb(235, 64, 52)' }}>a healthy lifestyle should be accessible to everyone</span>, which is why we offer our supplements at <span style={{ color: 'rgb(235, 64, 52)' }}>affordable prices</span>. Our carefully curated product selection includes <span style={{ color: 'rgb(235, 64, 52)' }}>protein, keratine, vitamins, and more</span>, all of which are <span style={{ color: 'rgb(235, 64, 52)' }}>proven to be effective and backed by research</span>.                        </Card.Text> 
                        <Card.Text>
                            But, we don't just stop there. At Nutrination, we also strive to provide our customers with <span style={{ color: 'rgb(235, 64, 52)' }}>unparalleled customer service</span>. It's our goal to make every customer's experience with us as <span style={{ color: 'rgb(235, 64, 52)' }}>seamless and enjoyable</span> as possible. Our <span style={{ color: 'rgb(235, 64, 52)' }}>friendly and knowledgeable</span> staff are always available to <span style={{ color: 'rgb(235, 64, 52)' }}>answer any questions or concerns</span>, so you can <span style={{ color: 'rgb(235, 64, 52)' }}>feel confident in your supplement choices</span>.                        </Card.Text> 
                    </Card.Body> 
            </Card>
            <Card className='shadow mb-3'>
              <Card.Header><h3>Our Team</h3></Card.Header>
              <ListGroup variant='flush'>
                <ListGroup.Item>
                  <Image
                    src='https://example.com/team-member1.jpg'
                    className='mr-3'
                    roundedCircle
                    style={{ width: '5rem', height: '5rem' }}
                  />
                  <h5>John Doe</h5>
                  <p className='text-muted'>Co-founder</p>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Image
                    src='https://example.com/team-member2.jpg'
                    className='mr-3'
                    roundedCircle
                    style={{ width: '5rem', height: '5rem' }}
                  />
                  <h5>Jane Doe</h5>
                  <p className='text-muted'>Co-founder</p>
                </ListGroup.Item>
              </ListGroup>
            </Card>
          </Col>
        </Row>
      )}
    </div>
  );
}

export default AboutUsScreen;
