import React from "react";
import { Nav } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";

function CheckoutSteps({ step1, step2, step3, step4 }) {
    return (
        <div className="d-flex justify-content-between mb-4">
            <div className={step1 ? "font-weight-bold" : "text-muted"}>
                {step1 ? (
                    <Link to="/login">Login</Link>
                ) : (
                    "Login"
                )}
            </div>

            <div>
                {step2 ? (
                    <>
                        <span className="mx-2">&#8594;</span>
                    </>
                ) : null}
            </div>

            <div className={step2 ? "font-weight-bold" : "text-muted"}>
                {step2 ? (
                    <Link to="/shipping" style={{color: "#000"}} onMouseOver={e => e.target.style.color = "rgb(235, 64, 52)"} onMouseLeave={e => e.target.style.color = "#000"}>Shipping</Link>
                ) : (
                    "Shipping"
                )}
            </div>

            <div>
                {step3 ? (
                    <>
                        <span className="mx-2">&#8594;</span>
                    </>
                ) : null}
            </div>

            <div className={step3 ? "font-weight-bold" : "text-muted"}>
                {step3 ? (
                    <Link to="/payment" style={{color: "#000"}} onMouseOver={e => e.target.style.color = "rgb(235, 64, 52)"} onMouseLeave={e => e.target.style.color = "#000"}>Payment</Link>
                ) : (
                    "Payment"
                )}
            </div>

            <div>
                {step4 ? (
                    <>
                        <span className="mx-2">&#8594;</span>
                    </>
                ) : null}
            </div>

            <div className={step4 ? "font-weight-bold" : "text-muted"}>
                {step4 ? (
                    <Link to="/placeorder" style={{color: "#000"}} onMouseOver={e => e.target.style.color = "rgb(235, 64, 52)"} onMouseLeave={e => e.target.style.color = "#000"}>Place Order</Link>
                ) : (
                    "Place Order"
                )}
            </div>
        </div>
    );
}

export default CheckoutSteps;
