import React, {useState, useEffect} from "react";
import { Row, Col, Button } from "react-bootstrap";
import Product from "../components/Product";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { useDispatch, useSelector } from 'react-redux'
import { listProducts } from '../actions/productActions'


function HomeScreen() {
  const dispatch = useDispatch()
  const productList = useSelector(state => state.productList)
  const {error, loading, products} = productList
  const [showArrow, setShowArrow] = useState(false)

  useEffect(() => {
    dispatch(listProducts())

    const handleScroll = () => {
      setShowArrow(window.scrollY > window.innerHeight / 2)
    }

    window.addEventListener('scroll', handleScroll)

    return () => {
      window.removeEventListener('scroll', handleScroll)
    }

  }, [dispatch])

  const handleUpArrowClick = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    })
  }

  return (
    <div className="body">
      <h1>Latest Products</h1>

      {loading ? <Loader/> : error ? <Message variant='danger'>{error}</Message> :
        <>
          <Row>
            {products.map((product) => (
              <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                <Product product={product} />
              </Col>
            ))}
          </Row>
          {showArrow && (
            <Button
              variant="light"
              onClick={handleUpArrowClick}
              style={{
                position: "fixed",
                bottom: "20px",
                right: "20px",
                backgroundColor: "rgb(235, 64, 52)",
                color: "#fff",
                transition: "background-color 0.3s ease",
                transform: "translateY(0)"
              }}  
              onMouseEnter={(e) => { 
                e.target.style.backgroundColor = "rgb(195, 26, 13)";
                e.target.style.transform = "translateY(-5px)";
              }}
              onMouseLeave={(e) => { 
                e.target.style.backgroundColor = "rgb(235, 64, 52)";
                e.target.style.transform = "translateY(0)";
              }}
            >
              <i className="fas fa-arrow-up" style={{ fontSize: "48px", transition: "transform 1s ease", marginTop: "-3px"}}></i>
            </Button>
          )}
        </>
      }
    </div>
  );
}

export default HomeScreen;
