import React, {useState, useEffect} from 'react'
import { Form, Button, Col, FormGroup } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import FormContainer from "../components/FormContainer"
import CheckoutSteps from '../components/CheckoutSteps'
import { useNavigate } from "react-router-dom";
//import { savePaymentMethod } from '../actions/cartActions'


function PaymentScreen() {

    const cart = useSelector(state => state.cart)
    const {shippingAddress} = cart

    const dispatch = useDispatch()
    const navigate = useNavigate()

    const [paymentMethod, setPaymentMethod] = useState('PayPal')

    if (!shippingAddress.address) {
        navigate('/shipping')
    }

    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;

    const submitHandler = (e) => {
        e.preventDefault()

        if (userInfo) {
            //dispatch(savePaymentMethod(paymentMethod))
            navigate('/placeorder')
        } else navigate("/login?redirect=placeorder");
    }

    return (
        <FormContainer>
            <CheckoutSteps step1 step2 step3 />
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <h1 style={{ fontSize: '2em' }}>Payment Method</h1>
                <Form onSubmit={submitHandler} style={{ width: '100%' }}>
                    <Form.Group controlId="paypal" style={{ display: 'flex', justifyContent: 'center' }}>
                        <Form.Check
                            type="radio"
                            name="paymentMethod"
                            value="PayPal or Credit Card"
                            checked={paymentMethod === "PayPal or Credit Card"}
                            onChange={(e) => setPaymentMethod(e.target.value)}
                            style={{ fontSize: '1.5em' }}
                        >
                            <Form.Label style={{ marginBottom: 0 }}>PayPal or Credit Card</Form.Label>
                        </Form.Check>
                    </Form.Group>
                    <Button type="submit" variant="primary" style={{ padding: '1em', width: '100%' }}>
                        Continue
                    </Button>
                </Form>
            </div>
        </FormContainer>
    )
}

export default PaymentScreen
