import React, {useEffect} from 'react'
import { Link, useParams, useNavigate, useLocation } from 'react-router-dom'
import { Row, Col, ListGroup, Image, Form, Button, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import { addToCart } from '../actions/cartActions'


function CartScreen() {
    const currentUrl = window.location.href
    const url = new URL(currentUrl);
    const productId1 = url.pathname.split('/')[2]
    const qty = Number(url.searchParams.get('qty'))

    const dispatch = useDispatch()
    useEffect(() => {
        if(productId1){
            dispatch(addToCart(productId1, qty))
        } 
    }, [dispatch, productId1, qty])

    const cart = useSelector(state => state.cart)
    const { cartItems } = cart

    const navigate = useNavigate()
    function handleClick() {
        navigate(-1)
    }

    return (
        <Row>
            <Col md={8}>
                <h1>Shopping Cart</h1>
                {cartItems.length === 0 ? (
                <div>
                    <Link onClick={handleClick} className='btn btn-light my-3'>Go Back</Link>
                    <Message variant='info'>Your cart is empty</Message>
                </div>
                ) : (
                <div>
                    <ListGroup variant='flush'>
                        {cartItems.map(item => (
                            <ListGroup.Item key={item.product}>
                                <Row className="square border border-danger">
                                    <Col md={4}>
                                        <Image src={item.image} alt={item.name} fluid rounded/>
                                    </Col>
                                    <Col md={6}>
                                        <Link to={`/product/${item.product}`}>{item.name}</Link>
                                    </Col>
                                    <Col md={2}>
                                        ${item.price}
                                    </Col>
                                </Row>
                            </ListGroup.Item>
                        ))}
                    </ListGroup>
                    {/* Add more ListGroupItems here */}
                </div>
                )}
            </Col>

            <Col md={4}>
                {/* Add Cart summary here */}
            </Col>
        </Row>
    )
}

export default CartScreen
