import React, { useEffect } from "react";
import { Link, useParams, useNavigate, useLocation } from "react-router-dom";
import {
  Row,
  Col,
  ListGroup,
  Image,
  Form,
  Button,
  Card,
} from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Message from "../components/Message";
import { addToCart } from "../actions/cartActions";

function CartScreen() {
  const currentUrl = window.location.href;
  const url = new URL(currentUrl);
  const productId = url.pathname.split("/")[2];
  const qty = Number(url.searchParams.get("qty"));

  const dispatch = useDispatch();
  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);

  const removeFromCartHandler = () => {
    console.log('remove', productId)
  }

  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;

  const navigate = useNavigate();
  function handleClick() {
    navigate(-1);
  }

  return (
    <Row>
      <Col md={12}>
        <h1>Shopping Cart</h1>
        {cartItems.length === 0 ? (
          <div>
            <Link onClick={handleClick} className="btn btn-light my-3">
              Go Back
            </Link>
            <Message variant="info">Your cart is empty</Message>
          </div>
        ) : (
          <div>
            <ListGroup variant="flush">
              {cartItems.map((item) => (
                <ListGroup.Item key={item.product}>
                  <Row>
                    <Col md={2}>
                      <Image src={item.image} alt={item.name} fluid rounded />
                    </Col>
                    <Col md={3}>
                      <Link to={`/product/${item.product}`} style={{display: 'flex', justifyContent: 'center'}}>{item.name}</Link>
                    </Col>
                    <Col md={2} style={{display: 'flex', justifyContent: 'center'}}>${item.price}</Col>
                    <Col md={3}>
                      <Form.Control
                        as="select"
                        value={item.qty}
                        onChange={(e) =>
                          dispatch(addToCart(item.product, e.target.value))
                        }
                      >
                        {[...Array(item.countInStock).keys()].map((x) => (
                          <option key={x + 1} value={x + 1}>
                            {x + 1}
                          </option>
                        ))}
                      </Form.Control>
                    </Col>
                    <Col md={1}>
                      <Button type="button" variant="light" onClick={() => removeFromCartHandler(item.product)}>
                        <i className="fas fa-trash"></i>
                      </Button>
                    </Col>
                  </Row>
                </ListGroup.Item>
              ))}
            </ListGroup>
          </div>
        )}
      </Col>

      <Col md={4}>
        <Card>
            <ListGroup variant='flush'>
                <ListGroup.Item>
                    <h2>Subtotal </h2>
                </ListGroup.Item>
            </ListGroup>
        </Card>
      </Col>
    </Row>
  );
}

export default CartScreen;
